macro.dbtvault.default__t_link.macro_sql:
{%- macro default__t_link(src_pk, src_fk, src_payload, src_eff, src_ldts, src_source, source_model) -%}

{{- dbtvault.check_required_parameters(src_pk=src_pk, src_fk=src_fk, src_eff=src_eff,
                                       src_ldts=src_ldts, src_source=src_source,
                                       source_model=source_model) -}}

{%- set src_pk = dbtvault.escape_column_names(src_pk) -%}
{%- set src_fk = dbtvault.escape_column_names(src_fk) -%}
{%- set src_payload = dbtvault.escape_column_names(src_payload) -%}
{%- set src_eff = dbtvault.escape_column_names(src_eff) -%}
{%- set src_ldts = dbtvault.escape_column_names(src_ldts) -%}
{%- set src_source = dbtvault.escape_column_names(src_source) -%}

{%- set source_cols = dbtvault.expand_column_list(columns=[src_pk, src_fk, src_payload, src_eff, src_ldts, src_source]) -%}
{%- set fk_cols = dbtvault.expand_column_list([src_fk]) -%}

{{ dbtvault.prepend_generated_by() }}

WITH stage AS (
    SELECT {{ source_cols | join(', ') }}
    FROM {{ ref(source_model) }}
    {%- if model.config.materialized == 'vault_insert_by_period' %}
    WHERE __PERIOD_FILTER__
    AND {{ dbtvault.multikey(src_pk, condition='IS NOT NULL') }}
    AND {{ dbtvault.multikey(fk_cols, condition='IS NOT NULL') }}
    {%- elif model.config.materialized == 'vault_insert_by_rank' %}
    WHERE __RANK_FILTER__
    AND {{ dbtvault.multikey(src_pk, condition='IS NOT NULL') }}
    AND {{ dbtvault.multikey(fk_cols, condition='IS NOT NULL') }}
    {%- else %}
    WHERE {{ dbtvault.multikey(src_pk, condition='IS NOT NULL') }}
    AND {{ dbtvault.multikey(fk_cols, condition='IS NOT NULL') }}
    {%- endif %}
),
records_to_insert AS (
    SELECT DISTINCT {{ dbtvault.prefix(source_cols, 'stg') }}
    FROM stage AS stg
    {% if dbtvault.is_any_incremental() -%}
    LEFT JOIN {{ this }} AS tgt
    ON {{ dbtvault.multikey(src_pk, prefix=['stg','tgt'], condition='=') }}
    WHERE {{ dbtvault.multikey(src_pk, prefix='tgt', condition='IS NULL') }}
    {%- endif %}
)

SELECT * FROM records_to_insert

{%- endmacro -%}

macro.dbtvault.xts.macro_sql:
{%- macro xts(src_pk, src_satellite, src_ldts, src_source, source_model) -%}
    {{- adapter.dispatch('xts', 'dbtvault')(src_pk=src_pk,
                                            src_satellite=src_satellite,
                                            src_ldts=src_ldts,
                                            src_source=src_source,
                                            source_model=source_model) -}}
{%- endmacro -%}

macro.dbtvault.default__xts.macro_sql:
{%- macro default__xts(src_pk, src_satellite, src_ldts, src_source, source_model) -%}

{%- set src_pk = dbtvault.escape_column_names(src_pk) -%}
{%- set src_ldts = dbtvault.escape_column_names(src_ldts) -%}
{%- set src_source = dbtvault.escape_column_names(src_source) -%}

{{ dbtvault.prepend_generated_by() }}

{%- if not (source_model is iterable and source_model is not string) -%}
    {%- set source_model = [source_model] -%}
{%- endif %}

{{ 'WITH ' }}
{%- for src in source_model %}
    {%- for satellite in src_satellite.items() -%}
        {%- set satellite_name = (satellite[1]['sat_name'].values() | list) [0] -%}
        {%- set hashdiff = (satellite[1]['hashdiff'].values() | list) [0] %}

        satellite_{{ satellite_name }}_from_{{ src }} AS (
            SELECT {{ dbtvault.prefix([src_pk], 's') }}, s.{{ dbtvault.escape_column_names(hashdiff) }} AS HASHDIFF, s.{{ dbtvault.escape_column_names(satellite_name) }} AS SATELLITE_NAME, s.{{ src_ldts }}, s.{{ src_source }}
            FROM {{ ref(src) }} AS s
            WHERE {{ dbtvault.multikey(src_pk, prefix='s', condition='IS NOT NULL') }}
        ),
    {%- endfor %}
{%- endfor %}

union_satellites AS (
    {%- for src in source_model %}
        {%- for satellite in src_satellite.items() %}
    SELECT * FROM satellite_{{ (satellite[1]['sat_name'].values() | list) [0] }}_from_{{ src }}
            {%- if not loop.last %}
    UNION ALL
            {%- endif %}
        {%- endfor %}
        {%- if not loop.last %}
    UNION ALL
        {%- endif %}
    {%- endfor %}
),

records_to_insert AS (
    SELECT DISTINCT union_satellites.* FROM union_satellites
    {%- if dbtvault.is_vault_insert_by_period() or is_incremental() %}
    LEFT JOIN {{ this }} AS d
    ON (union_satellites.{{ 'HASHDIFF' }} = d.{{ 'HASHDIFF' }}
        AND union_satellites.{{ src_ldts }} = d.{{ src_ldts }}
        AND union_satellites.{{ 'SATELLITE_NAME' }} = d.{{ 'SATELLITE_NAME' }}
    )
    WHERE {{ dbtvault.prefix(['HASHDIFF'], 'd') }} IS NULL
    AND {{ dbtvault.prefix([ src_ldts ], 'd') }} IS NULL
    AND {{ dbtvault.prefix([ 'SATELLITE_NAME' ], 'd') }} IS NULL
    {%- endif %}
)

SELECT * FROM records_to_insert

{%- endmacro -%}

macro.dbtvault.sqlserver__bridge.macro_sql:
{%- macro sqlserver__bridge(src_pk, as_of_dates_table, bridge_walk, stage_tables_ldts, src_ldts, source_model) -%}

{{- dbtvault.check_required_parameters(source_model=source_model, src_pk=src_pk,
                                       bridge_walk=bridge_walk,
                                       stage_tables_ldts=stage_tables_ldts,
                                       src_ldts=src_ldts) -}}

{%- set src_pk = dbtvault.escape_column_names(src_pk) -%}
{%- set src_ldts = dbtvault.escape_column_names(src_ldts) -%}

{{ dbtvault.prepend_generated_by() }}

{%- if (as_of_dates_table is none) and execute -%}
    {%- set error_message -%}
    "Bridge error: Missing as_of_dates table configuration. A as_of_dates_table must be provided."
    {%- endset -%}
    {{- exceptions.raise_compiler_error(error_message) -}}
{%- endif -%}

{#- Acquiring the source relation for the AS_OF table -#}
{%- if as_of_dates_table is mapping and as_of_dates_table is not none -%}
    {%- set source_name = as_of_dates_table | first -%}
    {%- set source_table_name = as_of_dates_table[source_name] -%}
    {%- set source_relation = source(source_name, source_table_name) -%}
{%- elif as_of_dates_table is not mapping and as_of_dates_table is not none -%}
    {%- set source_relation = ref(as_of_dates_table) -%}
{%- endif -%}

{%- set max_datetime = var('max_datetime', '9999-12-31 23:59:59.9999999') -%}

{#- Stating the dependencies on the stage tables outside of the If STATEMENT -#}
{% for stg in stage_tables_ldts -%}
    {{- "-- depends_on: " ~ ref(stg) -}}
{%- endfor %}

{#- Setting the new AS_OF dates CTE name -#}
{%- if dbtvault.is_any_incremental() -%}
    {%- set new_as_of_dates_cte = 'NEW_ROWS_AS_OF'  -%}
{%- else -%}
    {%- set new_as_of_dates_cte = 'AS_OF' -%}
{%- endif %}

WITH as_of AS (
     SELECT a.AS_OF_DATE
     FROM {{ source_relation }} AS a
     WHERE a.AS_OF_DATE <= GETDATE()
),

{%- if dbtvault.is_any_incremental() %}

last_safe_load_datetime AS (
    SELECT MIN(LOAD_DATETIME) AS LAST_SAFE_LOAD_DATETIME
    FROM (
    {%- filter indent(width=8) -%}
    {%- for stg in stage_tables_ldts -%}
        {%- set stage_ldts =(stage_tables_ldts[stg])  -%}
        {{ "SELECT MIN(" ~ stage_ldts ~ ") AS LOAD_DATETIME FROM " ~ ref(stg) }}
        {{ "UNION ALL" if not loop.last }}
    {% endfor -%}
    {%- endfilter -%}
    ) AS l
),

as_of_grain_old_entries AS (
    SELECT DISTINCT AS_OF_DATE
    FROM {{ this }}
),

as_of_grain_lost_entries AS (
    SELECT a.AS_OF_DATE
    FROM as_of_grain_old_entries AS a
    LEFT OUTER JOIN as_of AS b
        ON a.AS_OF_DATE = b.AS_OF_DATE
    WHERE b.AS_OF_DATE IS NULL
),

as_of_grain_new_entries AS (
    SELECT a.AS_OF_DATE
    FROM as_of AS a
    LEFT OUTER JOIN as_of_grain_old_entries AS b
        ON a.AS_OF_DATE = b.AS_OF_DATE
    WHERE b.AS_OF_DATE IS NULL
),

min_date AS (
    SELECT min(AS_OF_DATE) AS MIN_DATE
    FROM as_of
),

new_rows_pks AS (
    SELECT {{ dbtvault.prefix([src_pk], 'h') }}
    FROM {{ ref(source_model) }} AS h
    WHERE h.{{ src_ldts }} >= (SELECT LAST_SAFE_LOAD_DATETIME FROM last_safe_load_datetime)
),

new_rows_as_of AS (
    SELECT AS_OF_DATE
    FROM as_of
    WHERE as_of.AS_OF_DATE >= (SELECT LAST_SAFE_LOAD_DATETIME FROM last_safe_load_datetime)
    UNION
    SELECT as_of_date
    FROM as_of_grain_new_entries
),

overlap_pks AS (
    SELECT {{ dbtvault.prefix([src_pk], 'p') }}
    FROM {{ this }} AS p
    INNER JOIN {{ ref(source_model) }} as h
        ON {{ dbtvault.multikey(src_pk, prefix=['p','h'], condition='=') }}
    WHERE p.AS_OF_DATE >= (SELECT MIN_DATE FROM min_date)
        AND p.AS_OF_DATE < (SELECT LAST_SAFE_LOAD_DATETIME FROM last_safe_load_datetime)
        AND p.AS_OF_DATE NOT IN (SELECT AS_OF_DATE FROM as_of_grain_lost_entries)
),

overlap_as_of AS (
    SELECT AS_OF_DATE
    FROM as_of AS p
    WHERE p.AS_OF_DATE >= (SELECT MIN_DATE FROM min_date)
        AND p.AS_OF_DATE < (SELECT LAST_SAFE_LOAD_DATETIME FROM last_safe_load_datetime)
        AND p.AS_OF_DATE NOT IN (SELECT AS_OF_DATE FROM as_of_grain_lost_entries)
),

overlap AS (
    SELECT
        {{ dbtvault.prefix([src_pk], 'a') }},
        b.AS_OF_DATE
        {%- for bridge_step in bridge_walk.keys() -%}
            {%- set link_table = dbtvault.escape_column_names(bridge_walk[bridge_step]['link_table']) -%}
            {%- set link_pk = dbtvault.escape_column_names(bridge_walk[bridge_step]['link_pk']) -%}
            {%- set bridge_link_pk = dbtvault.escape_column_names(bridge_walk[bridge_step]['bridge_link_pk']) -%}
            {%- set eff_sat_table = dbtvault.escape_column_names(bridge_walk[bridge_step]['eff_sat_table']) -%}
            {%- set bridge_end_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['bridge_end_date']) -%}
            {%- set bridge_load_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['bridge_load_date']) -%}
            {%- set eff_sat_end_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['eff_sat_end_date']) -%}
            {%- set eff_sat_load_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['eff_sat_load_date']) -%}
            {%- filter indent(width=8) %}
            {{ ',' ~ link_table ~ '.' ~ link_pk ~ ' AS ' ~ bridge_link_pk }}
            {{ ',' ~ eff_sat_table ~ '.' ~ eff_sat_end_date ~ ' AS ' ~ bridge_end_date }}
            {{ ',' ~ eff_sat_table ~ '.' ~ eff_sat_load_date ~' AS ' ~ bridge_load_date }}
            {%- endfilter -%}
        {% endfor %}
    FROM overlap_pks AS a
    INNER JOIN overlap_as_of AS b
        ON (1=1)
    {%- set loop_vars = namespace(lastlink = '', last_link_fk = '') -%}
    {%- for bridge_step in bridge_walk.keys() -%}
        {%- set current_link = bridge_walk[bridge_step]['link_table'] -%}
        {%- set current_eff_sat = bridge_walk[bridge_step]['eff_sat_table'] -%}
        {%- set link_pk = dbtvault.escape_column_names(bridge_walk[bridge_step]['link_pk']) -%}
        {%- set link_fk1 = dbtvault.escape_column_names(bridge_walk[bridge_step]['link_fk1']) -%}
        {%- set link_fk2 = dbtvault.escape_column_names(bridge_walk[bridge_step]['link_fk2']) -%}
        {%- set eff_sat_pk = dbtvault.escape_column_names(bridge_walk[bridge_step]['eff_sat_pk']) -%}
        {%- set eff_sat_load_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['eff_sat_load_date']) -%}
        {%- if loop.first %}
    LEFT JOIN {{ ref(current_link) }} AS {{ dbtvault.escape_column_names(current_link) }}
        ON a.{{ src_pk }} = {{ dbtvault.escape_column_names(current_link) }}.{{ link_fk1 }}
        {%- else %}
    LEFT JOIN {{ ref(current_link) }} AS {{ dbtvault.escape_column_names(current_link) }}
        ON {{ loop_vars.last_link }}.{{ loop_vars.last_link_fk2 }} = {{ dbtvault.escape_column_names(current_link) }}.{{ link_fk1 }}
        {%- endif %}
    INNER JOIN {{ ref(current_eff_sat) }} AS {{ dbtvault.escape_column_names(current_eff_sat) }}
        ON {{ dbtvault.escape_column_names(current_eff_sat) }}.{{ eff_sat_pk }} = {{ dbtvault.escape_column_names(current_link) }}.{{ link_pk }}
        AND {{ dbtvault.escape_column_names(current_eff_sat) }}.{{ eff_sat_load_date }} <= b.AS_OF_DATE
        {%- set loop_vars.last_link = current_link -%}
        {%- set loop_vars.last_link_fk2 = link_fk2 -%}
    {% endfor %}
),
{%- endif %}

new_rows AS (
    SELECT
        {{ dbtvault.prefix([src_pk], 'a') }},
        b.AS_OF_DATE
        {%- for bridge_step in bridge_walk.keys() -%}
            {%- set link_table = dbtvault.escape_column_names(bridge_walk[bridge_step]['link_table']) -%}
            {%- set link_pk = dbtvault.escape_column_names(bridge_walk[bridge_step]['link_pk']) -%}
            {%- set bridge_link_pk = dbtvault.escape_column_names(bridge_walk[bridge_step]['bridge_link_pk']) -%}
            {%- set eff_sat_table = dbtvault.escape_column_names(bridge_walk[bridge_step]['eff_sat_table']) -%}
            {%- set bridge_end_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['bridge_end_date']) -%}
            {%- set bridge_load_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['bridge_load_date']) -%}
            {%- set eff_sat_end_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['eff_sat_end_date']) -%}
            {%- set eff_sat_load_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['eff_sat_load_date']) -%}
            {%- filter indent(width=8) -%}
            {{ ',' ~ link_table ~'.'~ link_pk ~' AS '~ bridge_link_pk }}
            {{ ',' ~ eff_sat_table ~ '.' ~ eff_sat_end_date ~ ' AS ' ~ bridge_end_date }}
            {{ ',' ~ eff_sat_table ~ '.' ~ eff_sat_load_date ~ ' AS ' ~ bridge_load_date }}
            {%- endfilter -%}
        {% endfor %}
    FROM {{ ref(source_model) }} AS a
    INNER JOIN {{ new_as_of_dates_cte }} AS b
        ON (1=1)
    {%- set loop_vars = namespace(lastlink = '', last_link_fk = '') %}
    {%- for bridge_step in bridge_walk.keys() -%}
        {%- set current_link = bridge_walk[bridge_step]['link_table'] -%}
        {%- set current_eff_sat = bridge_walk[bridge_step]['eff_sat_table'] -%}
        {%- set link_pk = dbtvault.escape_column_names(bridge_walk[bridge_step]['link_pk']) -%}
        {%- set link_fk1 = dbtvault.escape_column_names(bridge_walk[bridge_step]['link_fk1']) -%}
        {%- set link_fk2 = dbtvault.escape_column_names(bridge_walk[bridge_step]['link_fk2']) -%}
        {%- set eff_sat_pk = dbtvault.escape_column_names(bridge_walk[bridge_step]['eff_sat_pk']) -%}
        {%- set eff_sat_load_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['eff_sat_load_date']) -%}
        {%- if loop.first  %}
    LEFT JOIN {{ ref(current_link) }} AS {{ dbtvault.escape_column_names(current_link) }}
        ON a.{{ src_pk }} = {{ dbtvault.escape_column_names(current_link) }}.{{ link_fk1 }}
        {%- else %}
    LEFT JOIN {{ ref(current_link) }} AS {{ dbtvault.escape_column_names(current_link) }}
        ON {{ loop_vars.last_link }}.{{ loop_vars.last_link_fk2 }} = {{ dbtvault.escape_column_names(current_link) }}.{{ link_fk1 }}
        {%- endif %}
    INNER JOIN {{ ref(current_eff_sat) }} AS {{ dbtvault.escape_column_names(current_eff_sat) }}
        ON {{ dbtvault.escape_column_names(current_eff_sat) }}.{{ eff_sat_pk }} = {{ dbtvault.escape_column_names(current_link) }}.{{ link_pk }}
        AND {{ dbtvault.escape_column_names(current_eff_sat) }}.{{ eff_sat_load_date }} <= b.AS_OF_DATE
        {%- set loop_vars.last_link = dbtvault.escape_column_names(current_link) -%}
        {%- set loop_vars.last_link_fk2 = link_fk2 -%}
    {% endfor %}
),

{# Full data from bridge walk(s) -#}
all_rows AS (
    SELECT * FROM new_rows
    {%- if dbtvault.is_any_incremental() %}
    UNION ALL
    SELECT * FROM overlap
    {%- endif %}
),

{# Select most recent set of relationship key(s) for each as of date -#}
candidate_rows AS (
    SELECT *
    FROM
    (
    SELECT *,
        ROW_NUMBER() OVER (
            PARTITION BY AS_OF_DATE,
                {%- for bridge_step in bridge_walk.keys() -%}
                {% set bridge_link_pk = dbtvault.escape_column_names(bridge_walk[bridge_step]['bridge_link_pk']) -%}
                    {%- if loop.first %}
                {{ bridge_link_pk }}
                    {%- else %}
                {{ ','~ bridge_link_pk }}
                    {%- endif -%}
                {%- endfor %}
            ORDER BY
                {%- for bridge_step in bridge_walk.keys() -%}
                {% set bridge_load_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['bridge_load_date']) %}
                    {%- if loop.first %}
                {{ bridge_load_date ~' DESC' }}
                    {%- else %}
                {{ ','~ bridge_load_date ~' DESC' }}
                    {%- endif -%}
                {%- endfor %}
            ) AS row_num
    FROM all_rows
    ) AS a
    WHERE a.row_num = 1
),

bridge AS (
    SELECT
        {{ dbtvault.prefix([src_pk], 'c') }},
        c.AS_OF_DATE
        {%- for bridge_step in bridge_walk.keys() -%}
        {% set bridge_link_pk = dbtvault.escape_column_names(bridge_walk[bridge_step]['bridge_link_pk']) -%}
        {{ ',c.' ~ bridge_link_pk }}
        {%- endfor %}
    FROM candidate_rows AS c
        {%- for bridge_step in bridge_walk.keys() -%}
            {%- set bridge_end_date = dbtvault.escape_column_names(bridge_walk[bridge_step]['bridge_end_date']) -%}
            {%- if loop.first %}
    WHERE CONVERT(DATE, c.{{ bridge_end_date }}) = CONVERT(DATE, '{{ max_datetime }}')
            {%- else %}
        AND CONVERT(DATE, c.{{ bridge_end_date }}) = CONVERT(DATE, '{{ max_datetime }}')
            {%- endif -%}
        {%- endfor %}
)

SELECT * FROM bridge

{%- endmacro -%}

macro.dbtvault.sqlserver__eff_sat.macro_sql:
{%- macro sqlserver__eff_sat(src_pk, src_dfk, src_sfk, src_start_date, src_end_date, src_eff, src_ldts, src_source, source_model) -%}

{{- dbtvault.check_required_parameters(src_pk=src_pk, src_dfk=src_dfk, src_sfk=src_sfk,
                                       src_start_date=src_start_date, src_end_date=src_end_date,
                                       src_eff=src_eff, src_ldts=src_ldts, src_source=src_source,
                                       source_model=source_model) -}}

{%- set src_pk = dbtvault.escape_column_names(src_pk) -%}
{%- set src_dfk = dbtvault.escape_column_names(src_dfk) -%}
{%- set src_sfk = dbtvault.escape_column_names(src_sfk) -%}
{%- set src_start_date = dbtvault.escape_column_names(src_start_date) -%}
{%- set src_end_date = dbtvault.escape_column_names(src_end_date) -%}
{%- set src_eff = dbtvault.escape_column_names(src_eff) -%}
{%- set src_ldts = dbtvault.escape_column_names(src_ldts) -%}
{%- set src_source = dbtvault.escape_column_names(src_source) -%}

{%- set source_cols = dbtvault.expand_column_list(columns=[src_pk, src_dfk, src_sfk, src_start_date, src_end_date, src_eff, src_ldts, src_source]) -%}
{%- set fk_cols = dbtvault.expand_column_list(columns=[src_dfk, src_sfk]) -%}
{%- set dfk_cols = dbtvault.expand_column_list(columns=[src_dfk]) -%}
{%- set is_auto_end_dating = config.get('is_auto_end_dating', default=false) %}

{{- dbtvault.prepend_generated_by() }}

{%- set max_datetime = var('max_datetime', '9999-12-31 23:59:59.9999999') %}

WITH source_data AS (
    SELECT {{ dbtvault.prefix(source_cols, 'a', alias_target='source') }}
    FROM {{ ref(source_model) }} AS a
    WHERE {{ dbtvault.multikey(src_dfk, prefix='a', condition='IS NOT NULL') }}
    AND {{ dbtvault.multikey(src_sfk, prefix='a', condition='IS NOT NULL') }}
    {%- if model.config.materialized == 'vault_insert_by_period' %}
    AND __PERIOD_FILTER__
    {%- elif model.config.materialized == 'vault_insert_by_rank' %}
    AND __RANK_FILTER__
    {%- endif %}
),

{%- if dbtvault.is_any_incremental() %}

{# Selecting the most recent records for each link hashkey -#}
latest_records AS (
    SELECT *
    FROM
    (
        SELECT {{ dbtvault.alias_all(source_cols, 'b') }},
               ROW_NUMBER() OVER (
                    PARTITION BY {{ dbtvault.prefix([src_pk], 'b') }}
                    ORDER BY b.{{ src_ldts }} DESC
               ) AS row_num
        FROM {{ this }} AS b
    ) l
    WHERE l.row_num = 1
),

{# Selecting the open records of the most recent records for each link hashkey -#}
latest_open AS (
    SELECT {{ dbtvault.alias_all(source_cols, 'c') }}
    FROM latest_records AS c
    WHERE CONVERT(DATE, c.{{ src_end_date }}) = CONVERT(DATE, '{{ max_datetime }}')
),

{# Selecting the closed records of the most recent records for each link hashkey -#}
latest_closed AS (
    SELECT {{ dbtvault.alias_all(source_cols, 'd') }}
    FROM latest_records AS d
    WHERE CONVERT(DATE, d.{{ src_end_date }}) != CONVERT(DATE, '{{ max_datetime }}')
),

{# Identifying the completely new link relationships to be opened in eff sat -#}
new_open_records AS (
    SELECT DISTINCT
        {{ dbtvault.prefix([src_pk], 'f') }},
        {{ dbtvault.alias_all(fk_cols, 'f') }},
        {%- if is_auto_end_dating %}
        f.{{ src_eff }} AS {{ src_start_date }},
        {% else %}
        f.{{ src_start_date }} AS {{ src_start_date }},
        {% endif %}
        f.{{ src_end_date }} AS {{ src_end_date }},
        f.{{ src_eff }} AS {{ src_eff }},
        f.{{ src_ldts }},
        f.{{ src_source }}
    FROM source_data AS f
    LEFT JOIN latest_records AS lr
    ON {{ dbtvault.multikey(src_pk, prefix=['f','lr'], condition='=') }}
    WHERE {{ dbtvault.multikey(src_pk, prefix='lr', condition='IS NULL') }}
),

{# Identifying the currently closed link relationships to be reopened in eff sat -#}
new_reopened_records AS (
    SELECT DISTINCT
        {{ dbtvault.prefix([src_pk], 'lc') }},
        {{ dbtvault.alias_all(fk_cols, 'lc') }},
        {%- if is_auto_end_dating %}
        g.{{ src_eff }} AS {{ src_start_date }},
        {% else %}
        g.{{ src_start_date }} AS {{ src_start_date }},
        {% endif %}
        g.{{ src_end_date }} AS {{ src_end_date }},
        g.{{ src_eff }} AS {{ src_eff }},
        g.{{ src_ldts }},
        g.{{ src_source }}
    FROM source_data AS g
    INNER JOIN latest_closed AS lc
    ON {{ dbtvault.multikey(src_pk, prefix=['g','lc'], condition='=') }}
    WHERE CAST((g.{{ src_end_date }}) AS DATE) = CAST(('{{ max_datetime }}') AS DATE)
),

{%- if is_auto_end_dating %}

{# Creating the closing records -#}
{# Identifying the currently open relationships that need to be closed due to change in SFK(s) -#}
new_closed_records AS (
    SELECT DISTINCT
        {{ dbtvault.prefix([src_pk], 'lo') }},
        {{ dbtvault.alias_all(fk_cols, 'lo') }},
        lo.{{ src_start_date }} AS {{ src_start_date }},
        h.{{ src_eff }} AS {{ src_end_date }},
        h.{{ src_eff }} AS {{ src_eff }},
        h.{{ src_ldts }},
        lo.{{ src_source }}
    FROM source_data AS h
    INNER JOIN latest_open AS lo
    ON {{ dbtvault.multikey(src_dfk, prefix=['lo', 'h'], condition='=') }}
    WHERE ({{ dbtvault.multikey(src_sfk, prefix=['lo', 'h'], condition='<>', operator='OR') }})
),

{#- else if (not) is_auto_end_dating -#}
{% else %}

new_closed_records AS (
    SELECT DISTINCT
        {{ dbtvault.prefix([src_pk], 'lo') }},
        {{ dbtvault.alias_all(fk_cols, 'lo') }},
        h.{{ src_start_date }} AS {{ src_start_date }},
        h.{{ src_end_date }} AS {{ src_end_date }},
        h.{{ src_eff }} AS {{ src_eff }},
        h.{{ src_ldts }},
        lo.{{ src_source }}
    FROM source_data AS h
    LEFT JOIN Latest_open AS lo
    ON lo.{{ src_pk }} = h.{{ src_pk }}
    LEFT JOIN latest_closed AS lc
    ON lc.{{ src_pk }} = h.{{ src_pk }}
    WHERE CAST((h.{{ src_end_date }}) AS DATE) != CAST(('{{ max_datetime }}') AS DATE)
    AND lo.{{ src_pk }} IS NOT NULL
    AND lc.{{ src_pk }} IS NULL
),

{#- end if is_auto_end_dating -#}
{%- endif %}

records_to_insert AS (
    SELECT * FROM new_open_records
    UNION
    SELECT * FROM new_reopened_records
    UNION
    SELECT * FROM new_closed_records
)

{#- else if not dbtvault.is_any_incremental() -#}
{%- else %}

records_to_insert AS (
    SELECT {{ dbtvault.alias_all(source_cols, 'i') }}
    FROM source_data AS i
)

{#- end if not dbtvault.is_any_incremental() -#}
{%- endif %}

SELECT * FROM records_to_insert
{%- endmacro -%}

macro.dbtvault.sqlserver__hub.macro_sql:
{%- macro sqlserver__hub(src_pk, src_nk, src_ldts, src_source, source_model) -%}

{{- dbtvault.check_required_parameters(src_pk=src_pk, src_nk=src_nk,
                                       src_ldts=src_ldts, src_source=src_source,
                                       source_model=source_model) -}}

{%- set src_pk = dbtvault.escape_column_names(src_pk) -%}
{%- set src_nk = dbtvault.escape_column_names(src_nk) -%}
{%- set src_ldts = dbtvault.escape_column_names(src_ldts) -%}
{%- set src_source = dbtvault.escape_column_names(src_source) -%}

{%- set source_cols = dbtvault.expand_column_list(columns=[src_pk, src_nk, src_ldts, src_source]) -%}

{%- if model.config.materialized == 'vault_insert_by_rank' %}
    {%- set source_cols_with_rank = source_cols + dbtvault.escape_column_names([config.get('rank_column')]) -%}
{%- endif -%}

{{ dbtvault.prepend_generated_by() }}

{{ 'WITH ' -}}

{%- if not (source_model is iterable and source_model is not string) -%}
    {%- set source_model = [source_model] -%}
{%- endif -%}

{%- set ns = namespace(last_cte= "") -%}

{%- for src in source_model -%}

{%- set source_number = loop.index | string -%}

row_rank_{{ source_number }} AS (
    {%- if model.config.materialized == 'vault_insert_by_rank' %}
    SELECT {{ source_cols_with_rank | join(', ') }}
    {%- else %}
    SELECT {{ source_cols | join(', ') }}
    {%- endif %}
    FROM
    (
        {%- if model.config.materialized == 'vault_insert_by_rank' %}
        SELECT {{ dbtvault.prefix(source_cols_with_rank, 'rr') }},
        {%- else %}
        SELECT {{ dbtvault.prefix(source_cols, 'rr') }},
        {%- endif %}
               ROW_NUMBER() OVER(
                   PARTITION BY {{ dbtvault.prefix([src_pk], 'rr') }}
                   ORDER BY {{ dbtvault.prefix([src_ldts], 'rr') }}
               ) AS row_number
        FROM {{ ref(src) }} AS rr
        WHERE {{ dbtvault.multikey(src_pk, prefix='rr', condition='IS NOT NULL') }}
    ) h
